import React, { Component } from 'react';
import Image1 from '../../assets/img/uvod/uvod_large_2_1920.jpg'
import Image2 from '../../assets/img/uvod/uvod_large_3_1920.jpg'
import Image3 from '../../assets/img/uvod/uvod_large_4.jpg'
import Image4 from '../../assets/img/uvod/uvod_large_5.jpg'
import LightGallery from 'lightgallery/react';
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-zoom.css';
import 'lightgallery/css/lg-thumbnail.css';
import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';


function ImageGallery(props) {

    const title = props.title
    const text = props.text
    const thumbnail = props.data.thumbnail
    const image = props.data.image
    return (
        <div className="sluzba max-width">
            <div className='sluzba-text-wrapper'>
                <h2 className='sluzba-title'>{title}</h2>
                <p>{text}</p>
            </div>
            <LightGallery
                speed={500}
                plugins={[lgThumbnail, lgZoom]}
            >
                <a href={Image1}>
                    <img alt="img1" src={Image1} className='gallery-img'/>
                </a>
                <a href={Image1}>
                    <img alt="img2" src={Image1} className='gallery-img'/>
                </a>

            </LightGallery>
        </div>
    );

}
export default ImageGallery
