import React, { Component } from 'react';
import LightGallery from 'lightgallery/react';
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-zoom.css';
import 'lightgallery/css/lg-thumbnail.css';
import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';
import sluzbyData from './sluzbyData.json'


function ImageGallery(props) {

    const photoType = props.photoType

    const navrhTri = sluzbyData.triNavrh.map((item) => {
        return item

    })
    const navrhDva = sluzbyData.dvaNavrh.map((item) => {
        return item

    })




    const title = props.title
    const text = props.text
    return (
        <div className="sluzba max-width">
            <div className='sluzba-text-wrapper'>
                <h2 id={title} className='sluzba-title'>{title}</h2>
                <p>{text}</p>
            </div>
            <LightGallery
                speed={500}
                plugins={[lgThumbnail, lgZoom]}
            >
                {navrhDva.map((item, index) => (
                    <a href={item.image} key={index}>
                    <img alt="img1" src={item.thumbnail} className='gallery-img'/>
                    </a>

                ))}



            </LightGallery>
        </div>
    );

}
export default ImageGallery
