import React, { useEffect } from "react"
import Glide, { Controls, Breakpoints, Autoplay } from '@glidejs/glide/dist/glide.modular.esm'
import Image1 from '../../assets/img/uvod/uvod_large_2_1920.jpg'
import Image2 from '../../assets/img/uvod/uvod_large_3_1920.jpg'
import '@glidejs/glide/dist/css/glide.core.min.css'
import '@glidejs/glide/dist/css/glide.theme.min.css'
import './slider.css'


const ImageGlider = () => {
  const slider = new Glide('.glide', {
    type: 'slider',
    autoplay: 4000,
  });
  useEffect(() => {
    return () => slider.mount({ Controls, Breakpoints, Autoplay })
  }, [slider])
  const GlideTrackStyles = {

  }
  const GlideSlideImageStyles = {
    width: '100%',
    objectFit: 'cover'
  }
  const GlideArrowStyles = {
    marginLeft: '20px',
    marginRight: '20px'
  }
  const GlideArrowRightStyles = {
    marginLeft: '20px',
    marginRight: '20px',
    right: '0'
  }


  return (
    <section className="hero">
      <h1 className="none">Lenka Design</h1>
      {" "}
      <div className='glide'>
        <div className='glide__track' data-glide-el='track' style={GlideTrackStyles}>
          <ul className='glide__slides' >
            <li className='glide__slide slider'><img src={Image1} style={GlideSlideImageStyles} alt="" /></li>
            <li className='glide__slide slider'><img src={Image2} style={GlideSlideImageStyles} alt="" /></li>
            <li className='glide__slide slider'><img src={Image1} style={GlideSlideImageStyles} alt="" /></li>
            <li className='glide__slide slider'><img src={Image1} style={GlideSlideImageStyles} alt="" /></li>
          </ul>
          <div className='glide__arrows' data-glide-el='controls'>
          <button className='glide__arrow glide__arrow--prev' style={GlideArrowStyles} data-glide-dir='<'>prev</button>
          <button className='glide__arrow glide__arrow--next' data-glide-dir='>' style={GlideArrowRightStyles} >next</button>
        </div>
          <div class="glide__bullets" data-glide-el="controls[nav]">
    <button class="glide__bullet" data-glide-dir="=0"></button>
    <button class="glide__bullet" data-glide-dir="=1"></button>
    <button class="glide__bullet" data-glide-dir="=2"></button>
    <button class="glide__bullet" data-glide-dir="=3"></button>
        </div>
        </div>

      </div>
      </section>
  )
}

export default ImageGlider
